import unittest, math
from fudge.core.math._xData import axes, XYs

__metaclass__ = type

class TestXYs( unittest.TestCase ):

    def setUp( self ):
        self.vl1 = axes.axes( )
        self.vl1[0] = axes.axis( 'energy_in', 0, 'eV', interpolation = axes.interpolationXY( axes.linearToken, axes.linearToken ) )
        self.vl1[1] = axes.axis( 'crossSection', 1, 'b' )
        self.pXY1 = XYs.XYs( self.vl1, [ [ 1, 0 ], [ 3, 2 ], [ 4, 1 ] ], 1e-3, safeDivide = True )
        self.pXY2 = XYs.XYs( self.vl1, [ [ 1, 0 ], [ 4, 2 ] ], 1e-3, safeDivide = True )
        self.pXY3 = XYs.XYs( self.vl1, [ [ 1, 1 ], [ 2, 2 ], [ 4, 3 ], [ 7, 1 ] ], 1e-3, safeDivide = True )
        self.pXY4 = XYs.XYs( self.vl1, [ [ 1, 0 ], [ 2.5, 2 ], [ 3, 3 ], [ 7, 0 ] ], 1e-3, safeDivide = True )

    def test_toString( self ):
        self.assertEqual( str( self.pXY1.axes ), 'label="energy_in", index="0", unit="eV", interpolation="lin-lin"\nlabel="crossSection", index="1", unit="b"' )
        self.assertEqual( self.pXY1.toString( ), '   1.00000000e+00   0.00000000e+00\n   3.00000000e+00   2.00000000e+00\n   4.00000000e+00   1.00000000e+00\n' )
        self.assertEqual( self.pXY2.toString( ), '   1.00000000e+00   0.00000000e+00\n   4.00000000e+00   2.00000000e+00\n' )
        self.assertEqual( self.pXY3.toString( ), '   1.00000000e+00   1.00000000e+00\n   2.00000000e+00   2.00000000e+00\n   4.00000000e+00   3.00000000e+00\n   7.00000000e+00   1.00000000e+00\n' )
        self.assertEqual( self.pXY4.toString( ), '   1.00000000e+00   0.00000000e+00\n   2.50000000e+00   2.00000000e+00\n   3.00000000e+00   3.00000000e+00\n   7.00000000e+00   0.00000000e+00\n' )

    def test_addition( self ):
        self.assertEqual( (self.pXY1 + '320 b').toString( ) , '   1.00000000e+00   3.20000000e+02\n   3.00000000e+00   3.22000000e+02\n   4.00000000e+00   3.21000000e+02\n' )

