    def test_createFromFunc_exp( self ):
        """
        Make an XYs from exp(-x), then run some tests ::
            * test 20 values of the interpolation table vs. real thing
            * integral of function from 1e-5 -- 20 ( approximately infinity ;) 
        """
        def myExp(x,*args): return math.exp(-x)
        xmin=1e-5
        xmax=20.0
        nx=20
        axes_ = axes.axes( dimension = 2 )
        axes_[0] = axes.axis( 'energy_in', 0, 'eV', interpolation = axes.interpolationXY( axes.linearToken, axes.linearToken ) )
        axes_[1] = axes.axis( 'junk', 1, 'b' )
        approxExp = XYs.XYs.createFromFunction( \
            axes_, \
            Xs=[xmin, xmax],\
            func=myExp, \
            parameters=[], \
            accuracy=1e-8, \
            biSectionMax=20, \
            checkForRoots = False, \
            infill = 1, \
            safeDivide = 1 )
        for ix in range(nx):
            x = xmin+(xmax-xmin)*ix/nx
            self.assertAlmostEqual(myExp(x), approxExp.getValue(x))
        self.assertAlmostEqual(approxExp.integrate(xmin,xmax),myExp(xmin)-myExp(xmax))
        
