    def test_createFromFunc_lorentzian( self ):
        """
        Make an XYs from a Lorentzian ::
        
            L(x) = 1.0/( (x-10)^2+1 )
        
        then run some tests ::
            * test 20 values of the interpolation table vs. real thing
            * integral of function from 1e-5 -- 20.  This gets the left tail and 1/2 the right one.  
              Sage says the answer is approximately arctan(10)+1.47112757529
        """
        def myLorentzian(x,*args): return 1.0/((x-10.0)*(x-10.0)+1.0)
        xmin=1e-5
        xmax=20.0
        nx=20
        axes_ = axes.axes( dimension = 2 )
        axes_[0] = axes.axis( 'energy_in', 0, 'eV', interpolation = axes.interpolationXY( axes.linearToken, axes.linearToken ) )
        axes_[1] = axes.axis( 'junk', 1, 'b' )
        approxLorentz = XYs.XYs.createFromFunction( \
            axes_, \
            Xs=[xmin, xmax],\
            func=myLorentzian, \
            parameters=[], \
            accuracy=1e-8, \
            biSectionMax=20, \
            checkForRoots = False, \
            infill = 1, \
            safeDivide = 1 )
        for ix in range(nx):
            x = xmin+(xmax-xmin)*ix/nx
            self.assertAlmostEqual(myLorentzian(x), approxLorentz.getValue(x))
        self.assertAlmostEqual(approxLorentz.integrate(xmin,xmax),math.atan(10.0) + 1.47112757529)
   
